    def resetGroupBox(self):
        self.txtNhietDo.setText("")
        self.txtDoAm.setText("")
        self.txtMucGio.setText("")
        self.txtLuongMua.setText("")
        self.txtLuongTuyet.setText("")
        self.txtNhietDo.setFocus()
    def closeIt(self):
        msg = QMessageBox()
        msg.setIcon(QMessageBox.Question)
        msg.setWindowTitle("Thoát")
        msg.setText("Bạn có muốn thoát không?")
        msg.setStandardButtons(QMessageBox.Yes | QMessageBox.No)
        reply = msg.exec()
        if reply == QMessageBox.Yes:
            sys.exit()
    def run(self):
        nhietdo = float(self.txtNhietDo.text())
        doam = float(self.txtDoAm.text())
        mucgio = float(self.txtMucGio.text())
        luongmua = float(self.txtLuongMua.text())
        luongtuyet = float(self.txtLuongTuyet.text())
        may = int(self.cboMay.currentText())
        x = PredictWeather(nhietdo,doam,mucgio,may,luongmua,luongtuyet)
        self.lblDuDoan.setText(x['description'])
        self.lblThoiTiet.setPixmap(QtGui.QPixmap(":/Images/images/"+x['img']))
        self.lblChuY.setText(x['notice'])



